import { Component } from '@angular/core';
import { User } from './user-model';

@Component({
  selector: 'app-user-component',
  templateUrl: './user-component.component.html',
  styleUrls: ['./user-component.component.css']
})
export class UserComponentComponent {

  users: User[] = [];

  constructor () {
      this.users = [
        {
          userid:1,
          username: "name 1",
          address:"Address 1",
          pincode: "pincode 1"
        },
        {
          userid:2,
          username: "name 2",
          address:"Address 2",
          pincode: "pincode 2"
        },
        {
          userid:3,
          username: "name 3",
          address:"Address 3",
          pincode: "pincode 3"
        },
      ]
  }

  ngOnInit():void {

  }
}

<h3><span class="badge bg-secondary">All Users</span></h3>
<table class="table">
    <thead>
      <tr>
        <th scope="col">Sl No</th>
        <th scope="col">User ID</th>
        <th scope="col">User Name</th>
        <th scope="col">Address</th>
        <th scope="col">Pin Code</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let user of users;let i = index">
        <th scope="row">(i+1)</th>
        <td>{{user.userid}}</td>
        <td>{{user.username}}</td>
        <td>{{user.address}}</td>
        <td>{{user.pincode}}</td>
      </tr>
    </tbody>
  </table>
