import { Injectable } from '@angular/core';
import { User } from './user-component/user-model';

@Injectable({
  providedIn: 'root'
})
export class MockServiceService {

  users: User[] = [
    {
      userid:1,
      username: "name 1",
      address:"Address 1",
      pincode: "pincode 1"
    },
    {
      userid:2,
      username: "name 2",
      address:"Address 2",
      pincode: "pincode 2"
    },
    {
      userid:3,
      username: "name 3",
      address:"Address 3",
      pincode: "pincode 3"
    },
  ];

  constructor() { }

  updateUser(user:User) {
    console.log(user);
    for (let i = 0; i < this.users.length; i++) {
      if (user.userid == this.users[i].userid) {
        let usr = new User();
        usr.userid = user.userid;
        usr.username = user.username;
        usr.address = user.address;
        usr.pincode = user.pincode;
        break;
      }
    }
    alert('User updated event called ' + user.userid + "|" + user.username)
  }

  insertUser(user:User) {
    
    let usr = new User();
        usr.userid = this.users.length + 1;
        usr.username = user.username;
        usr.address = user.address;
        usr.pincode = user.pincode;
    this.users.push(usr);
  }

  getAllUsers() {
    return this.users;
  }
}
