import { Component, Input } from '@angular/core';
import { User } from '../user-component/user-model';

@Component({
  selector: 'app-user-table',
  templateUrl: './user-table.component.html',
  styleUrls: ['./user-table.component.css']
})
export class UserTableComponent {

  @Input()
  users!: User[];

  constructor() {

  }
}
<table class="table">
    <thead>
      <tr>
        <th scope="col">Sl No</th>
        <th scope="col">User ID</th>
        <th scope="col">User Name</th>
        <th scope="col">Address</th>
        <th scope="col">Pin Code</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let user of users;let i = index" style="background-color: lightblue;">
        <td scope="row">{{(i+1)}}</td>
        <td *ngIf="i % 2 == 0" style="background-color: lightblue;">{{user.userid}}</td>
        <td *ngIf="i % 2 == 1">{{user.userid}}</td>
        <td>{{user.username}}</td>
        <td>{{user.address}}</td>
        <td>{{user.pincode}}</td>
      </tr>
    </tbody>
  </table>
