import { Component } from '@angular/core';
import { User } from './user-model';

@Component({
  selector: 'app-user-component',
  templateUrl: './user-component.component.html',
  styleUrls: ['./user-component.component.css']
})
export class UserComponentComponent {

  users: User[] = [];

  constructor () {
      this.users = [
        {
          userid:1,
          username: "name 1",
          address:"Address 1",
          pincode: "pincode 1"
        },
        {
          userid:2,
          username: "name 2",
          address:"Address 2",
          pincode: "pincode 2"
        },
        {
          userid:3,
          username: "name 3",
          address:"Address 3",
          pincode: "pincode 3"
        },
      ]
  }

  updateUser(user:User) {
alert('User updated event called ' + user.userid + "|" + user.username)
  }

  ngOnInit():void {

  }
}

<h3 class="text-center"><span class="badge bg-secondary">All Users</span></h3>
<app-user-table [users]="users" (updateEventEmitter)="updateUser" class="text-center"></app-user-table>
