import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { User } from '../user-component/user-model';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})
export class UserFormComponent {


  @Input()  user!: User;
  @Input()  show! :boolean;
  @Input() newuser! : boolean;

  @Output() newUserEvent = new EventEmitter();
  @Output() updateUserEvent = new EventEmitter();

  constructor () {
    console.log(this.user)
  }

  ngOnInit():void {
    console.log(this.user)
  }

  save() {
    alert('save | ' + this.newuser)
    if (this.newuser) {
      alert('new user :: ' + this.user.userid + " | " + this.user.username)
      this.newUserEvent.emit(this.user)
      this.user = new User;
    } else {
      this.updateUserEvent.emit(this.user);
      this.user = new User;
      alert('update user :: ' + this.user)
    }
    this.show = false;
  }

  hideForm() {
    this.show = false;
  }
}
<div [hidden]="show"> {{show}}
<form #userForm="ngForm" style="border-style: outset;border-radius: 25px;padding: 10px;" novalidate onsubmit="return false;">
    <div class="form-group row">
      <label for="userid" class="col-sm-2 col-form-label">User Id</label>
      <div class="col-sm-10">
        <input type="text" disabled class="form-control-plaintext" id="userid" name="userid" placeholder="Selected ID"
         [readOnly]="true" ngModel required #userid="ngModel">
      </div>
    </div>
    <div class="form-group row">
      <label for="username" class="col-sm-2 col-form-label">User Name</label>
      <div class="col-sm-10">
        <input type="text" class="form-control" id="username" name="username"
         placeholder="User Name" ngModel required #username="ngModel">
      </div>
    </div>
    <div class="form-group row">
        <label for="address" class="col-sm-2 col-form-label">Address</label>
        <div class="col-sm-10">
          <input type="text" class="form-control" id="address" name="address" placeholder="Address"
          ngModel required #address="ngModel">
        </div>
      </div>
      <div class="form-group row">
        <label for="pincode" class="col-sm-2 col-form-label">Pin Code</label>
        <div class="col-sm-10">
          <input type="text" class="form-control" id="pincode" name="pincode" placeholder="Pin"
           ngModel required #pincode="ngModel">
        </div>
      </div>
      <div class="form-group row">
        <div class="col-sm-6">
            <button type="button" class="col-sm-2 btn btn-primary mb-2" (click)="save()">Save</button>
          </div>
        <div class="col-sm-6">
            <button type="button" class="col-sm-2 btn btn-primary mb-2" (click)="hideForm()">Cancel</button>
        </div>
      </div>

  </form>
  </div>
